export class Project{constructor(n){this.bindCarouselToModal();this.bindVideoToModal();this.Utils=n}bindVideoToModal(){document.getElementById("project-body").querySelectorAll("a.video").forEach(n=>{n.addEventListener("click",n=>{n.preventDefault();const t=n.target.getAttribute("data-src"),r=n.target.getAttribute("data-title"),i=`<video controls autoplay><source src="/video/${t}" type="video/mp4"/></video>`;Utils.showModal({html:i});this.bindStopVideoOnModalClose()})})}bindCarouselToModal(){document.getElementById("project-body").querySelectorAll("a.gallery").forEach(n=>{n.addEventListener("click",n=>{n.preventDefault();const t=n.target.getAttribute("data-id"),u=n.target.getAttribute("data-title");document.getElementById(`carousel-${t}`).querySelectorAll(".carousel-inner img").forEach(n=>{n.setAttribute("src",`/image/projects${n.getAttribute("data-src")}`)});Utils.showModal({title:"",html:""});let r=document.getElementById(`carousel-${t}`).parentNode.cloneNode(!0);const i=`carousel-${t}-modal`;r.setAttribute("id",i);document.querySelector("#Modal .modal-body").appendChild(r);$(i).carousel();$(i).bind("slid",function(){})})})}bindStopVideoOnModalClose(){$("#Modal .close").on("click",function(){const n=document.querySelector("video");n.pause();n.currentTime=0})}}